    private void jTabbedPane1StateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_jTabbedPane1StateChanged
       int iTabindex = 0;               // jTabbedPane1 index 저장
       String strTabTitle = null;       // jTabbedPane1 Title 저장
       
       iTabindex = jTabbedPane1.getSelectedIndex();                                 // jTabbedPane1 index 변환
       strTabTitle = jTabbedPane1.getTitleAt(iTabindex) + " 정보를 볼 수 있습니다.";     // jTabbedPane Title 변환
    }//GEN-LAST:event_jTabbedPane1StateChanged

