    public void ProgressBar_Value() {
       try{
           for(int idx=0; idx<=100; idx++){
               jProgressBar1.setValue(idx);
               Thread.sleep(100);
               lblTitle.setText(idx + "% 검색 중");
           }
       } catch(Exception e){
           this.setTitle(" 검색 중단 ");
       }
    }
